import { NextPage } from 'next';
import Layout from '../../components/layout'
import Navbar from "../../components/base/Navbar"
import {useQuery} from "@apollo/react-hooks"
import gql from 'graphql-tag';
import CategoryHead from "../../components/section/CategoryHead"
import styled from 'styled-components'
import {AuthorNames} from "../../components/util"

const Title = styled.h2`
font-family: Cormorant;
font-style: normal;
font-weight: bold;
font-size: 28px;
line-height: 34px;
color: #000000;

`
const Section = ({slug}) => {
  let categoryTitle = slug.replace(/-/g, ' ');
  const { loading, error, data } = useQuery(CategoryDocument, {
    variables: {category: categoryTitle}
  })
  if (loading) return <p>Loading Post...</p>;
  if (error) return <p>Something wrong happened!</p>;
  let articles = data.posts.nodes
    return (<>
    <Navbar /> 
    <Layout>
      <CategoryHead category={categoryTitle} />
      <p>{categoryTitle}</p>
      {articles.map((article) => (<>
        <Title>{article.title}</Title>
        <AuthorNames authors={article.coAuthors} />
        <>{article.excerpt}</>
        <>{article.date}</>

      </>))}
    </Layout>
    </>)
}

const CategoryDocument = gql`
query CategoryPage($category: String) {
  posts(where:{categoryName: $category}){
    nodes{
      title
      excerpt
      date
      coAuthors{
        display_name
        slug
        job_title
      }
      issues{
        nodes{
          name
        }
      }
    }
  }
}

`

Section.getInitialProps = async ({ query }) => {
  return { slug: query.slug }
}

export default Section
